/**
 * add two numbers
 *
 * @param {number} a input
 * @param {number} b input
 *
 * @returns {number} addition of a and b
 */
function add(a, b) {
  return a + b;
};

/**
 * subtract two numbers
 *
 * @param {number} a input
 * @param {number} b input
 *
 * @returns {number} difference in a and b
 */
function subtract(a, b) {
  return a - b;
};

module.exports = {
  add,
  subtract,
};
